import { Badge } from '@material-ui/core';
import { Search, ShoppingCartOutlined } from '@material-ui/icons';
import React from 'react'
import styled from 'styled-components'
import { mobile } from '../responsive';
import {Link} from 'react-router-dom'
import {useSelector} from "react-redux"



const Container = styled.div`
    height: 60px;
    background-color: white;
    ${mobile({ height: "42px", width:"100%"})}
`;
const Wrapper = styled.div`
    padding: 10px 20px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    ${mobile({ padding: "9px 0px"})}
`;
const Left = styled.div`
    flex: 1;
    display: flex;
    align-items: center;

`;

const Logo = styled.h1`
    font-weight: bold;
    font-size: 35px;
    cursor: pointer;
    ${mobile({ fontSize: "20px"})}
`;



const Center = styled.div`
    flex: 2 ;
    text-align: left;
    ${mobile({ display:"none"})}

`;

const SearchContainer = styled.div`
    border: 0.5px solid lightgray ;
    margin-left: 25px;
    padding: 1px; 
    width: 80%;
    height: 30px;
    display: flex;
    align-items: center;

`;

const Input = styled.input` 
    border: none;
    width: 92%;
    height: 90%;
    align-items: flex-start;

`;

const Right = styled.div`
    flex: 1;
    display: flex;
    align-items: center;
    justify-content: flex-end;
    ${mobile({ flex:3, justifyContent: "left"})}
`;

const MenuItem = styled.div`
    font-size: 17px;
    cursor: pointer;
    margin-left: 25px; 
    font-weight: 600;
    ${mobile({ fontSize: "12px", marginLeft:"10px"})}
`;



 const Navbar = () => {
     const quantity = useSelector(state=>state.cart.quantity)

   return (
        <Container>
        <Wrapper>

        <Left>
        <Link to="/" style={{textDecoration:"none", color:"black"}}><Logo>KOTLAR</Logo></Link>
        </Left>

        <Center>     
        <SearchContainer> 
        <Input placeholder="Search"/>
        <Search style={{color:"gray", fontSize: 20, }} />
        </SearchContainer> 
        </Center>


        <Right>
        <Link to="/categories" style={{textDecoration:"none", color:"black"}}><MenuItem>CATEGORIES </MenuItem></Link>

        <Link to="/loginpage" style={{textDecoration:"none", color:"black"}}><MenuItem>PROFILE</MenuItem></Link>
        <Link to="/aboutus" style={{textDecoration:"none", color:"black"}}><MenuItem>ABOUT</MenuItem></Link>
        <Link to="/cart" style={{textDecoration:"none", color:"black"}}>
        <MenuItem>
        <Badge badgeContent={quantity} color="secondary"  >
        <ShoppingCartOutlined/>
        </Badge> 
        </MenuItem>
        </Link>
        </Right>
        </Wrapper>
         </Container >

   )
 }

 export default Navbar
